sys_ui_page.client_script:
var chartOptions = {
	chart: {
		renderTo : 'cost-planned-actual'
	},
	legend: {
		enabled: true
	},
	title: {
		text: ''
	},
	credits : {
		enabled : false
	},
	exporting : {
		enabled : false
	},
	tooltip: {
		formatter: function () {
			return '<b>' + this.series.xAxis.categories[this.point.x] + '</b><br></br><b>' +
			this.point.y + "</b> " + costData.yAxis_title;
		}
	},
	yAxis: {
		title: {
			text: 'Hours',
		}
	},
};
chartOptions['xAxis'] = costData.xAxis;
chartOptions['series'] =  costData.series;
chartOptions['yAxis']['title']['text'] = 'Total Estimated Cost';
new Highcharts.Chart(chartOptions);
